import React from "react";
import {
  Container,
  Row,
  NavbarBrand,
  Navbar,
  NavLink,
  NavbarToggler,
  Collapse
} from "reactstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

import { logout, areAuthItemsSet } from "../../utils/Auth/Auth";

const textStyle = {
  color: "white",
  textDecoration: "none",
  "& a:focus": {
    textDecoration: "none"
  },
  "& a:hover": {
    textDecoration: "none"
  },
  "& a:visited": {
    textDecoration: "none"
  },
  "& a:link": {
    textDecoration: "none"
  },
  "& a:active": {
    textDecoration: "none"
  }
};

const NavStyle = {
  backgroundColor: "#292f36"
};

const NavLinkWhite = {
  color: "white",
  cursor: "pointer",
  textAlign: "right"
};

const white = {
  color: "white"
};

class Header extends React.Component {
  constructor(props) {
    super(props);

    this.toggle = this.toggle.bind(this);
    this.state = {
      isOpen: false
    };
  }
  toggle() {
    this.setState({
      isOpen: !this.state.isOpen
    });
  }
  render() {
    return (
      <Navbar style={NavStyle}>
        <NavbarToggler style={white} onClick={this.toggle} />
        <Collapse isOpen={this.state.isOpen} navbar>
          <NavbarBrand style={textStyle} href="/">
            Jamocha Dashboard
          </NavbarBrand>
          {areAuthItemsSet() && (
            <NavLink style={NavLinkWhite} onClick={() => logout()}>
              <FontAwesomeIcon icon="sign-in-alt" /> Logout
            </NavLink>
          )}
          {!areAuthItemsSet() && (
            <NavLink style={NavLinkWhite} href="/login">
              <FontAwesomeIcon icon="sign-in-alt" /> Login
            </NavLink>
          )}
        </Collapse>
      </Navbar>
    );
  }
}

export default Header;
